mapper.select:
select

from user
where user_phone = #{userPhone} and user_password = #{userPassword}

mapper.insert:
insert into user (id, user_name, user_phone, 
  user_email, user_password, university, 
  user_identity, create_time, modified_time
  )
values (#{id,jdbcType=INTEGER}, #{userName,jdbcType=VARCHAR}, #{userPhone,jdbcType=CHAR}, 
  #{userEmail,jdbcType=VARCHAR}, #{userPassword,jdbcType=CHAR}, #{university,jdbcType=VARCHAR}, 
  #{userIdentity,jdbcType=TINYINT}, #{createTime,jdbcType=TIMESTAMP}, #{modifiedTime,jdbcType=TIMESTAMP}
  )
