mapper.select:
select user_id, user_name, project_id, role_name, role_id, permission_id, permission_name, permission_name_english, permission_description,
permission_description_english, permission_action, permission_type
from user_permission_view where user_id = #{userId} and project_id = #{projectId}

select user_id
from user_permission_view
where user_id = #{userId} and project_id = #{projectId} and permission_id = #{permissionId}
